/* Local DOMPurify fallback loader.
 * Intentionally inert: if CDN DOMPurify loaded, it does nothing.
 * If CDN load fails, app.js will show a degraded-sanitizer warning.
 */
(function () {
  if (typeof window === 'undefined' || typeof window.DOMPurify !== 'undefined') {
    return;
  }
  // Keep explicit marker for diagnostics.
  window.__domPurifyBundleLoaded = true;
})();
